import React, { useRef, useState, useEffect } from "react";
import Button from "@atlaskit/button";
import ResizeHandle from "./ResizeHandle";
import { token } from "@atlaskit/tokens";
import { useDraggable } from "@dnd-kit/core";
import TaskBox from "./TaskBox";
import Schedule from "./Schedule";

export default function Panel({
  panel,
  setPanels,
  tasks,
  setTasks,
  gridRef,
  cols,
  rows,
  activeId,
  hidden
}) {
  const panelRef = useRef(null);
  const [isResizing, setIsResizing] = useState(false);
  const [fullscreen, setFullscreen] = useState(false);
  const prev = useRef(null);

  // ⭐ Stable DND data so data.current never wipes out
  const stableData = React.useMemo(
    () => ({
      role: "panel",
      panelId: panel.id,
      fromRow: panel.row,
      fromCol: panel.col,
      width: panel.width,
      height: panel.height,
    }),
    [] // MUST stay empty
  );

  // ---- DRAG ENABLED UNLESS RESIZING ----
  const { attributes, listeners, setNodeRef } = useDraggable({
    id: panel.id,
    disabled: isResizing,
    data: stableData,
  });

  const dragListeners = isResizing ? {} : listeners;

  // ------------------------------------------------------
  // FULLSCREEN
  // ------------------------------------------------------
  const toggleFullscreen = () => {
    if (!fullscreen) {
      prev.current = { ...panel };
      setPanels((list) =>
        list.map((p) =>
          p.id === panel.id
            ? { ...p, row: 0, col: 0, width: cols, height: rows }
            : p
        )
      );
    } else {
      setPanels((list) =>
        list.map((p) =>
          p.id === panel.id ? { ...p, ...prev.current } : p
        )
      );
    }
    setFullscreen((f) => !f);
  };

  // ------------------------------------------------------
  // GRID TRACK UTILITIES
  // ------------------------------------------------------
  const getTrackInfo = () => {
    const data = gridRef.current?.dataset.sizes;
    return data ? JSON.parse(data) : null;
  };

  const colFromPx = (px) => {
    const { colSizes } = getTrackInfo();
    const rect = gridRef.current.getBoundingClientRect();
    const rel = (px - rect.left) / rect.width;
    const total = colSizes.reduce((a, b) => a + b, 0);

    let acc = 0;
    for (let i = 0; i < colSizes.length; i++) {
      acc += colSizes[i];
      if (rel < acc / total) return i;
    }
    return colSizes.length - 1;
  };

  const rowFromPx = (py) => {
    const { rowSizes } = getTrackInfo();
    const rect = gridRef.current.getBoundingClientRect();
    const rel = (py - rect.top) / rect.height;
    const total = rowSizes.reduce((a, b) => a + b, 0);

    let acc = 0;
    for (let i = 0; i < rowSizes.length; i++) {
      acc += rowSizes[i];
      if (rel < acc / total) return i;
    }
    return rowSizes.length - 1;
  };

  // ------------------------------------------------------
  // MOBILE + MOUSE RESIZE HANDLER
  // ------------------------------------------------------
  const beginResize = (event) => {
    event.preventDefault();
    event.stopPropagation();
    setIsResizing(true);

    let startX = event.clientX ?? event.touches?.[0]?.clientX;
    let startY = event.clientY ?? event.touches?.[0]?.clientY;

    const move = (ev) => {
      const clientX = ev.clientX ?? ev.touches?.[0]?.clientX;
      const clientY = ev.clientY ?? ev.touches?.[0]?.clientY;

      if (clientX == null || clientY == null) return;

      const newCol = colFromPx(clientX);
      const newRow = rowFromPx(clientY);

      const width = Math.max(1, newCol - panel.col + 1);
      const height = Math.max(1, newRow - panel.row + 1);

      const boundedW = Math.min(width, cols - panel.col);
      const boundedH = Math.min(height, rows - panel.row);

      setPanels((list) =>
        list.map((p) =>
          p.id === panel.id
            ? { ...p, width: boundedW, height: boundedH }
            : p
        )
      );

      startX = clientX;
      startY = clientY;
    };

    const stop = () => {
      setIsResizing(false);
      window.removeEventListener("mousemove", move);
      window.removeEventListener("mouseup", stop);

      window.removeEventListener("touchmove", move);
      window.removeEventListener("touchend", stop);
      window.removeEventListener("touchcancel", stop);
    };

    // Mouse
    window.addEventListener("mousemove", move);
    window.addEventListener("mouseup", stop);

    // Touch
    window.addEventListener("touchmove", move, { passive: false });
    window.addEventListener("touchend", stop);
    window.addEventListener("touchcancel", stop);
  };

  // ------------------------------------------------------
  // GRID AREA
  // ------------------------------------------------------
  const gridArea = `${panel.row + 1} / ${panel.col + 1} / 
                    ${panel.row + panel.height + 1} / 
                    ${panel.col + panel.width + 1}`;

  // ------------------------------------------------------
  // RENDER
  // ------------------------------------------------------
  return (
    <div
      ref={(el) => {
        panelRef.current = el;
        setNodeRef(el);
      }}
      {...attributes}
      style={{
        gridArea,
        background: token("elevation.surface", "rgba(17,17,17,0.8)"),
        borderRadius: 8,
        border: "1px solid #AAA",
        overflow: "hidden",
        userSelect: "none",

        opacity: hidden ? 0 : 1,
        visibility: hidden ? "hidden" : "visible",

        // ⭐ When dragging, click-through so grid cells receive pointer
        pointerEvents: activeId === panel.id ? "none" : "auto",

        zIndex: fullscreen ? 3 : 1,
        position: "relative",
      }}
    >
      <div style={{ display: activeId === panel.id ? "none" : "block" }}>
        {/* HEADER */}
        <div
          style={{
            background: "#DDE2EB",
            padding: "6px 12px",
            borderTopLeftRadius: 8,
            borderTopRightRadius: 8,
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            fontWeight: 600,
            position: "relative",
            zIndex: fullscreen ? 3 : 1,
          }}
        >
          <div style={{ pointerEvents: "none" }}>☰ Panel</div>

          <select
            value={panel.type}
            onChange={(e) => {
              const type = e.target.value;
              setPanels((list) =>
                list.map((p) =>
                  p.id === panel.id ? { ...p, type } : p
                )
              );
            }}
            style={{ marginLeft: 12, zIndex: 5 }}
          >
            <option value="taskbox">TaskBox</option>
            <option value="schedule">Schedule</option>
          </select>

          <Button spacing="compact" onClick={toggleFullscreen}>
            {fullscreen ? "Restore" : "Fullscreen"}
          </Button>

          {/* DRAG HANDLE */}
          <div
            {...dragListeners}
            style={{
              position: "absolute",
              left: 0,
              top: 0,
              bottom: 0,
              width: 50,
              background: "rgba(255,0,0,0.3)",
              cursor: isResizing ? "default" : "grab",
              zIndex: 10,
              touchAction: "none",
            }}
          />
        </div>

        {/* CONTENT */}
        <div
          style={{
            padding: 12,
            color: "white",
            height: "100%",
            overflow: "hidden",
            pointerEvents: activeId === panel.id ? "none" : "auto",
          }}
        >
          {panel.type === "taskbox" && (
            <TaskBox
              panel={panel}
              tasks={tasks}
              setTasks={setTasks}
              fromPanelId={panel.id}
              disabled={fullscreen}
            />
          )}

          {panel.type === "schedule" && (
            <Schedule
              panel={panel}
              tasks={tasks}
              setTasks={setTasks}
              fromPanelId={panel.id}
            />
          )}
        </div>

        {/* MOBILE-FRIENDLY RESIZE HANDLE */}
        <ResizeHandle
          onMouseDown={beginResize}
          onTouchStart={beginResize}
        />
      </div>
    </div>
  );
}
