import React from "react";
import { token } from "@atlaskit/tokens";

/**
 * PanelClone.jsx
 *
 * This is the drag-overlay version of a panel shown while dragging.
 * It MUST be lightweight and NOT rely on refs, useDraggable, or layout logic.
 */
export default function PanelClone({ panel }) {
  if (!panel) return null;

  return (
    <div
      style={{
        width: "160px",
        height: "120px",
        background: token("elevation.surface", "rgba(30,30,30,0.92)"),
        borderRadius: 8,
        border: "1px solid rgba(255,255,255,0.25)",
        boxShadow:
          "0px 4px 12px rgba(0,0,0,0.35), 0 0 0 1px rgba(0,0,0,0.3) inset",
        overflow: "hidden",
        pointerEvents: "none",

        // ⭐ smooth lifted effect
        transform: "scale(1.05)",
        opacity: 0.95,
        backdropFilter: "blur(4px)",
      }}
    >
      {/* Header */}
      <div
        style={{
          background: "#DDE2EB",
          padding: "6px 12px",
          fontWeight: 600,
          borderTopLeftRadius: 8,
          borderTopRightRadius: 8,
          color: "#111",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          height: 36,
        }}
      >
        <div style={{ opacity: 0.7 }}>☰ Panel</div>
        <div
          style={{
            background: "#AAA",
            padding: "2px 6px",
            borderRadius: 4,
            fontSize: 12,
            fontWeight: 700,
            opacity: 0.5,
          }}
        >
          Dragging
        </div>
      </div>

      {/* Content preview */}
      <div
        style={{
          padding: 12,
          fontSize: 13,
          color: "white",
          opacity: 0.8,
        }}
      >
        Panel content
      </div>
    </div>
  );
}
