export default function ResizeHandle({ onPointerDown }) {
  return (
    <div
      onPointerDown={onPointerDown}
      style={{
        position: "absolute",
        right: 0,
        bottom: 0,
        width: 24,
        height: 24,
        cursor: "nwse-resize",
        touchAction: "none",   // ⭐ critical for mobile
        background: "rgba(255,255,255,0.18)"
      }}
    />
  );
}
