import React, { useRef, useState, useEffect } from "react";
import {
  DndContext,
  PointerSensor,
  TouchSensor,
  useSensor,
  useSensors,
  DragOverlay
} from "@dnd-kit/core";

import Panel from "./Panel";
import PanelClone from "./PanelClone";

export default function Grid({
  rows,
  cols,
  panels,
  setPanels,
  toggleToolbar,
}) {
  const gridRef = useRef(null);
  const [activeId, setActiveId] = useState(null);

  // Prevent page scrolling during drag
  const lockScroll = () => {
    document.body.style.overflow = "hidden";
    document.documentElement.style.overflow = "hidden";
  };

  const unlockScroll = () => {
    document.body.style.overflow = "";
    document.documentElement.style.overflow = "";
  };

  // Track sizes (FR units)
  const [colSizes, setColSizes] = useState(() => Array(cols).fill(1));
  const [rowSizes, setRowSizes] = useState(() => Array(rows).fill(1));

  useEffect(() => setColSizes(Array(cols).fill(1)), [cols]);
  useEffect(() => setRowSizes(Array(rows).fill(1)), [rows]);

  useEffect(() => {
    if (gridRef.current) {
      gridRef.current.dataset.sizes = JSON.stringify({
        colSizes,
        rowSizes,
      });
    }
  }, [colSizes, rowSizes]);

  const colTemplate = colSizes.map((s) => `${s}fr`).join(" ");
  const rowTemplate = rowSizes.map((s) => `${s}fr`).join(" ");

  // Sensors â€” touch disabled for scroll stability
  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: { distance: 10 }
    }),
    useSensor(TouchSensor, {
      activationConstraint: { delay: 100, tolerance: 5 }
    })
  );

  const getPanel = (id) => panels.find((p) => p.id === id);

  const getCellFromPointer = (clientX, clientY) => {
    const rect = gridRef.current.getBoundingClientRect();
    const relX = (clientX - rect.left) / rect.width;
    const relY = (clientY - rect.top) / rect.height;

    const totalCols = colSizes.reduce((a, b) => a + b, 0);
    let acc = 0, col = 0;
    for (let i = 0; i < colSizes.length; i++) {
      acc += colSizes[i];
      if (relX < acc / totalCols) { col = i; break; }
    }

    const totalRows = rowSizes.reduce((a, b) => a + b, 0);
    acc = 0;
    let row = 0;
    for (let i = 0; i < rowSizes.length; i++) {
      acc += rowSizes[i];
      if (relY < acc / totalRows) { row = i; break; }
    }

    return { col, row };
  };

  const getPointerXY = (event) => {
    const s = event.active.data.current?.pointerStart;
    if (!s) return null;
    return { x: s.x + event.delta.x, y: s.y + event.delta.y };
  };

  const [hoverCell, setHoverCell] = useState(null);

  const handleDragStart = (event) => {
    lockScroll();          // â­ STOP PAGE SCROLLING
    setActiveId(event.active.id);

    const e = event.activatorEvent;
    if (e?.clientX != null && e?.clientY != null) {
      event.active.data.current = { pointerStart: { x: e.clientX, y: e.clientY } };
    }
  };

  const handleDragMove = (event) => {
    const pointer = getPointerXY(event);
    if (!pointer) return;
    const { col, row } = getCellFromPointer(pointer.x, pointer.y);
    setHoverCell({ col, row });
  };

  const handleDragEnd = (event) => {
    unlockScroll();        // â­ RESTORE SCROLLING
    setActiveId(null);

    const pointer = getPointerXY(event);
    if (!pointer) return;

    const panel = getPanel(event.active.id);
    if (!panel) return;

    const { col, row } = getCellFromPointer(pointer.x, pointer.y);

    setPanels(list =>
      list.map(p =>
        p.id === panel.id
          ? { ...p, col, row, width: 1, height: 1 }
          : p
      )
    );

    setHoverCell(null);
  };

  return (
    <DndContext
      sensors={sensors}
      onDragStart={handleDragStart}
      onDragMove={handleDragMove}
      onDragEnd={handleDragEnd}
    >
      <div
        style={{
          position: "absolute",
          inset: 0,
          background: "#1D2125",
          overflow: "hidden",   // â­ no scrolling at container level
          touchAction: "none"   // â­ absolutely required for mobile drag
        }}
      >
        {/* Wrench Button */}
        <div
          onClick={toggleToolbar}
          style={{
            position: "absolute",
            top: 10,
            right: 10,
            zIndex: 100,
            width: 42,
            height: 42,
            background: "#2C313A",
            border: "1px solid #555",
            borderRadius: 8,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            cursor: "pointer",
            color: "white"
          }}
        >
          ðŸ”§
        </div>

        {/* GRID */}
<div
  ref={gridRef}
  style={{
    position: "absolute",
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,

    // â­ adds a safe zone for the bottom row
    paddingBottom: "30px",

    display: "grid",
    gridTemplateColumns: colTemplate,
    gridTemplateRows: rowTemplate,
  }}>
          {/* Hover highlight */}
          {hoverCell && (
            <div
              style={{
                gridColumnStart: hoverCell.col + 1,
                gridRowStart: hoverCell.row + 1,
                gridColumnEnd: hoverCell.col + 2,
                gridRowEnd: hoverCell.row + 2,
                background: "rgba(100,160,255,0.35)",
                pointerEvents: "none",
                zIndex: 10,
              }}
            />
          )}

          {/* Background grid */}
          {[...Array(rows)].map((_, r) =>
            [...Array(cols)].map((_, c) => (
              <div
                key={`${r}-${c}`}
                style={{
                  background: (r + c) % 2 ? "#2A2F35" : "#23272C",
                  border: "1px solid #3B4046"
                }}
              />
            ))
          )}

          {/* Panels */}
          {panels.map((p) => (
            <Panel
              key={p.id}
              panel={p}
              setPanels={setPanels}
              gridRef={gridRef}
              cols={cols}
              rows={rows}
              activeId={activeId}
            />
          ))}
        </div>
      </div>

      <DragOverlay>
        {activeId && <PanelClone panel={getPanel(activeId)} />}
      </DragOverlay>
    </DndContext>
  );
}
