import React, { useRef, useState, useEffect } from "react";
import Button from "@atlaskit/button";
import ResizeHandle from "./ResizeHandle";
import { useDraggable } from "@dnd-kit/core";

export default function Panel({
  panel,
  setPanels,
  gridRef,
  cols,
  rows,
  activeId
}) {
  const panelRef = useRef(null);
  const [isResizing, setIsResizing] = useState(false);
  const prev = useRef(null);

  // Disable drag while resizing
  const { attributes, listeners, setNodeRef } = useDraggable({
    id: panel.id,
    disabled: isResizing
  });

  const dragListeners = isResizing ? {} : listeners;

  const getTrackInfo = () => {
    const data = gridRef.current?.dataset.sizes;
    return data ? JSON.parse(data) : null;
  };

  const colFromPx = (px) => {
    const { colSizes } = getTrackInfo();
    const rect = gridRef.current.getBoundingClientRect();
    const rel = (px - rect.left) / rect.width;
    const total = colSizes.reduce((a, b) => a + b, 0);

    let acc = 0;
    for (let i = 0; i < colSizes.length; i++) {
      acc += colSizes[i];
      if (rel < acc / total) return i;
    }
    return colSizes.length - 1;
  };

  const rowFromPx = (py) => {
    const { rowSizes } = getTrackInfo();
    const rect = gridRef.current.getBoundingClientRect();
    const rel = (py - rect.top) / rect.height;
    const total = rowSizes.reduce((a, b) => a + b, 0);

    let acc = 0;
    for (let i = 0; i < rowSizes.length; i++) {
      acc += rowSizes[i];
      if (rel < acc / total) return i;
    }
    return rowSizes.length - 1;
  };

  // RESIZE
  const beginResize = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsResizing(true);
  };

  useEffect(() => {
    if (!isResizing) return;

    const move = (e) => {
      const newCol = colFromPx(e.clientX);
      const newRow = rowFromPx(e.clientY);

      const width = Math.max(1, newCol - panel.col + 1);
      const height = Math.max(1, newRow - panel.row + 1);

      setPanels(list =>
        list.map(p =>
          p.id === panel.id
            ? {
                ...p,
                width: Math.min(width, cols - panel.col),
                height: Math.min(height, rows - panel.row)
              }
            : p
        )
      );
    };

    const stop = () => {
      setIsResizing(false);
    };

    window.addEventListener("mousemove", move);
    window.addEventListener("mouseup", stop);

    return () => {
      window.removeEventListener("mousemove", move);
      window.removeEventListener("mouseup", stop);
    };
  }, [isResizing, panel, cols, rows, setPanels]);

  const gridArea = `${panel.row + 1} / ${panel.col + 1} / 
                    ${panel.row + panel.height + 1} / 
                    ${panel.col + panel.width + 1}`;

  return (
    <div
      ref={(el) => {
        panelRef.current = el;
        setNodeRef(el);
      }}
      {...attributes}
      style={{
        gridArea,
        borderRadius: 8,
        background: "rgba(20,20,20,0.85)",
        border: "1px solid #999",
        position: "relative",
        userSelect: "none",
        touchAction: "none",   // ⭐ disables iOS scroll conflict
        opacity: activeId === panel.id ? 0 : 1
      }}
    >
      {/* HEADER */}
      <div
        style={{
          background: "#DEE1E6",
          padding: "6px 12px",
          borderTopLeftRadius: 8,
          borderTopRightRadius: 8,
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          fontWeight: 600,
          position: "relative"
        }}
      >
        <div style={{ pointerEvents: "none" }}>☰ Panel</div>

        {/* Drag handle */}
        <div
          {...dragListeners}
          style={{
            position: "absolute",
            left: 0,
            top: 0,
            width: 60,
            bottom: 0,
            cursor: isResizing ? "default" : "grab",
            touchAction: "none"
          }}
        />

        <Button appearance="subtle">Fullscreen</Button>
      </div>

      <div style={{ padding: 10, color: "white" }}>Panel content</div>

      <ResizeHandle onMouseDown={beginResize} />
    </div>
  );
}
