import React, { useContext } from "react";
import { useSortable } from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import { ScheduleContext } from "./ScheduleContext";

export default function SortableItem({ instanceId, containerId }) {
    const { instanceStoreRef } = useContext(ScheduleContext);

    const inst = instanceStoreRef.current[instanceId] || {};
    const label = inst.label ?? "Untitled";

    const {
        setNodeRef,
        attributes,
        listeners,
        transform,
        transition,
        isDragging
    } = useSortable({
        id: instanceId,
        data: {
            type: "task",
            instanceId,
            containerId
        }
    });

    const style = {
        transform: CSS.Transform.toString(transform),
        transition,
        opacity: isDragging ? 0.5 : 1,
        background: "#2F343A",
        border: "1px solid #444",
        borderRadius: 6,
        padding: "8px 10px",
        marginBottom: 6,
        color: "white",
        cursor: "grab",
        userSelect: "none",
        fontSize: 14
    };
const isDragDisabled = (eventTarget) => {
  return eventTarget.closest("[data-dndkit-disable-drag]");
};
    return (
       <div
  ref={setNodeRef}
  {...attributes}
  {...listeners}
  onTouchStart={(e) => {
    if (isDragDisabled(e.target)) {
      e.stopPropagation();
      return;
    }
  }}
  onMouseDown={(e) => {
    if (isDragDisabled(e.target)) {
      e.stopPropagation();
      return;
    }
  }}
  style={{
    transform: CSS.Transform.toString(transform),
    transition,
    touchAction: "none",
    ...style,
    touchAction: "none",
    WebkitUserSelect: "none",
    userSelect: "none",
  }}
>
         <button
  data-dndkit-disable-drag
  onClick={handleClick}
>
  Click me
</button>
            {label}
        </div>
    );
}
