export default function ResizeHandle({ onPointerDown }) {
  return (
    <div
      onPointerDown={onPointerDown}
      style={{
        position: "absolute",
        right: 6,         // ⭐ Inset away from the grid edge
        bottom: 6,        // ⭐ Fix: adds finger room
        width: 28,
        height: 28,
        borderRadius: 6,
        cursor: "nwse-resize",
        touchAction: "none",
        background: "rgba(255,255,255,0.2)",
        zIndex: 20,       // ensure it's above content
      }}
    />
  );
}
