import React, { useRef, useState, useEffect } from "react";
import {
  DndContext,
  PointerSensor,
  TouchSensor,
  useSensor,
  useSensors,
  DragOverlay
} from "@dnd-kit/core";

import Panel from "./Panel";
import PanelClone from "./PanelClone";

export default function Grid({
  rows,
  cols,
  panels,
  setPanels,
  toggleToolbar,
}) {
  const gridRef = useRef(null);
  const [activeId, setActiveId] = useState(null);

  // FR sizes
  const [colSizes, setColSizes] = useState(() => Array(cols).fill(1));
  const [rowSizes, setRowSizes] = useState(() => Array(rows).fill(1));

  useEffect(() => setColSizes(Array(cols).fill(1)), [cols]);
  useEffect(() => setRowSizes(Array(rows).fill(1)), [rows]);

  // Expose to panel
  useEffect(() => {
    if (gridRef.current) {
      gridRef.current.dataset.sizes = JSON.stringify({
        colSizes,
        rowSizes,
      });
    }
  }, [colSizes, rowSizes]);

  const colTemplate = colSizes.map((s) => `${s}fr`).join(" ");
  const rowTemplate = rowSizes.map((s) => `${s}fr`).join(" ");

  // --------------------------------------------
  //  TOUCH + MOUSE SENSOR FIX
  // --------------------------------------------
  const sensors = useSensors(
    useSensor(PointerSensor, {
      activationConstraint: { distance: 8 },
    }),
    useSensor(TouchSensor, {
      activationConstraint: { distance: 8 },
    })
  );

  const getPanel = (id) => panels.find((p) => p.id === id);

  // --------------------------------------------
  //  FIXED TOUCH/MOUSE COORD EXTRACTION
  // --------------------------------------------
  const getClientXY = (e) => {
    if (!e) return null;

    // Touch event
    if (e.touches && e.touches.length > 0) {
      return {
        x: e.touches[0].clientX,
        y: e.touches[0].clientY,
      };
    }

    // Mouse event
    if (e.clientX != null && e.clientY != null) {
      return { x: e.clientX, y: e.clientY };
    }

    return null;
  };

  const getPointerXY = (event) => {
    const start = event.active.data.current?.pointerStart;
    if (!start) return null;
    return {
      x: start.x + event.delta.x,
      y: start.y + event.delta.y,
    };
  };

  // Convert screen â†’ grid cell
  const getCellFromPointer = (clientX, clientY) => {
    const rect = gridRef.current.getBoundingClientRect();

    const relX = (clientX - rect.left) / rect.width;
    const relY = (clientY - rect.top) / rect.height;

    // column
    const totalCols = colSizes.reduce((a, b) => a + b, 0);
    let acc = 0;
    let col = 0;
    for (let i = 0; i < colSizes.length; i++) {
      acc += colSizes[i];
      if (relX < acc / totalCols) {
        col = i;
        break;
      }
    }

    // row
    const totalRows = rowSizes.reduce((a, b) => a + b, 0);
    acc = 0;
    let row = 0;
    for (let i = 0; i < rowSizes.length; i++) {
      acc += rowSizes[i];
      if (relY < acc / totalRows) {
        row = i;
        break;
      }
    }

    return { col, row };
  };

  // hover
  const [hoverCell, setHoverCell] = useState(null);

  // --------------------------------------------
  // DRAG START
  // --------------------------------------------
  const handleDragStart = (event) => {
    setActiveId(event.active.id);

    const coords = getClientXY(event.activatorEvent);

    if (coords) {
      event.active.data.current = {
        pointerStart: coords
      };
    }
  };

  // --------------------------------------------
  // DRAG MOVE
  // --------------------------------------------
  const handleDragMove = (event) => {
    const pointer = getPointerXY(event);
    if (!pointer || !gridRef.current) return;

    const { col, row } = getCellFromPointer(pointer.x, pointer.y);
    setHoverCell({ col, row });
  };

  // --------------------------------------------
  // DRAG END
  // --------------------------------------------
  const handleDragEnd = (event) => {
    const pointer = getPointerXY(event);
    setActiveId(null);

    if (!pointer) return;

    const panel = getPanel(event.active.id);
    if (!panel) return;

    const { col, row } = getCellFromPointer(pointer.x, pointer.y);

    setPanels((list) =>
      list.map((p) =>
        p.id === panel.id
          ? { ...p, col, row, width: 1, height: 1 }
          : p
      )
    );

    setHoverCell(null);
  };

  // --------------------------------------------
  // COLUMN + ROW RESIZE CODE (unchanged)
  // --------------------------------------------
  const resizeColumn = (i, movement) => {
    setColSizes((sizes) => {
      const next = i + 1;
      if (next >= sizes.length) return sizes;
      const copy = [...sizes];
      copy[i] += movement / 300;
      copy[next] -= movement / 300;
      copy[i] = Math.max(0.3, copy[i]);
      copy[next] = Math.max(0.3, copy[next]);
      return copy;
    });
  };

  const resizeRow = (i, movement) => {
    setRowSizes((sizes) => {
      const next = i + 1;
      if (next >= sizes.length) return sizes;
      const copy = [...sizes];
      copy[i] += movement / 300;
      copy[next] -= movement / 300;
      copy[i] = Math.max(0.3, copy[i]);
      copy[next] = Math.max(0.3, copy[next]);
      return copy;
    });
  };

  // --------------------------------------------
  // RENDER
  // --------------------------------------------
  return (
    <DndContext
      sensors={sensors}
      onDragStart={handleDragStart}
      onDragMove={handleDragMove}
      onDragEnd={handleDragEnd}
    >
      <div
        style={{
          position: "absolute",
          inset: 0,
          background: "#1D2125",
          overflow: "hidden",
        }}
      >
        {/* Wrench */}
        <div
          onClick={toggleToolbar}
          style={{
            position: "absolute",
            top: 10,
            right: 10,
            width: 42,
            height: 42,
            background: "#2C313A",
            border: "1px solid #444",
            borderRadius: 8,
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            color: "white",
            cursor: "pointer",
            zIndex: 2,
          }}
        >
          ðŸ”§
        </div>

        {/* MAIN GRID */}
        <div
          ref={gridRef}
          style={{
            position: "absolute",
            inset: 0,
            display: "grid",
            gridTemplateColumns: colTemplate,
            gridTemplateRows: rowTemplate,
          }}
        >
          {/* HOVER GRID HIGHLIGHT */}
          {hoverCell && (
            <div
              style={{
                gridColumnStart: hoverCell.col + 1,
                gridRowStart: hoverCell.row + 1,
                gridColumnEnd: hoverCell.col + 2,
                gridRowEnd: hoverCell.row + 2,
                background: "rgba(50,150,255,0.45)",
                pointerEvents: "none",
                zIndex: 5,
              }}
            />
          )}

          {/* Checkerboard */}
          {[...Array(rows)].map((_, r) =>
            [...Array(cols)].map((_, c) => {
              const dark = (r + c) % 2 === 0;
              return (
                <div
                  key={`${r}-${c}`}
                  style={{
                    background: dark ? "#22272B" : "#2C333A",
                    border: "1px solid #3F444A",
                  }}
                />
              );
            })
          )}

          {/* Panels */}
          {panels.map((p) => (
            <Panel
              key={p.id}
              panel={p}
              setPanels={setPanels}
              gridRef={gridRef}
              cols={cols}
              rows={rows}
              activeId={activeId}
            />
          ))}
        </div>
      </div>

      {/* Drag Preview */}
      <DragOverlay>
        {activeId && <PanelClone panel={getPanel(activeId)} />}
      </DragOverlay>
    </DndContext>
  );
}
