// TaskBox.js
import { useDraggable } from "@dnd-kit/core";

export default function TaskBox({ panel, tasks, disabled }) {
  return (
    <div style={{ padding: 8, overflow: "hidden" }}>
      {panel.tasks.map((taskId) => {
        const task = tasks.find((t) => t.id === taskId);
        if (!task) return null;

        // Logical ID (task) vs unique draggableId (this instance)
        const draggableId = `panel-${panel.id}-task-${taskId}`;

        return (
          <TaskItem
            key={draggableId}
            draggableId={draggableId}
            taskId={taskId}
            label={task.label}
            fromPanelId={panel.id}
            fromSlotId={null}
            disabled={disabled}
          />
        );
      })}
    </div>
  );
}

// ⭐ Exported so Schedule can reuse it
export function TaskItem({
  draggableId,
  taskId,
  label,
  fromPanelId,
  fromSlotId,
  disabled,
}) {
  const { setNodeRef, attributes, listeners, transform } = useDraggable({
    id: String(draggableId),  // unique per instance
    disabled,
    data: {
      role: "task",           // Grid uses this to branch task vs panel
      taskId: String(taskId), // logical task id
      fromPanelId: fromPanelId ?? null,
      fromSlotId: fromSlotId ?? null,
    },
  });
console.log("TaskItem rendered:", {
  draggableId,
  taskId,
  label,
});
  return (
    <div
      ref={setNodeRef}
      {...attributes}
      {...listeners}
      style={{
        padding: 8,
        marginBottom: 6,
        background: "#334",
        color: "white",
        borderRadius: 6,
        cursor: disabled ? "not-allowed" : "grab",
        userSelect: "none",
        transform: transform
          ? `translate3d(${transform.x}px, ${transform.y}px, 0)`
          : undefined,
      }}
    >
      {label}
    </div>
  );
}
